import React from 'react';
import { connect } from 'react-redux';
import { fetchPosts } from '../../actions';
import Jenn from '../../img/jen_pic_blog.jpg';

class BlogList extends React.Component {
  componentDidMount(){
      this.props.fetchPosts();
  }

  renderList(){
    var imageLink;
    if(post.author === 'art'){
      imageLink = require('../../img/art_pic_blog.jpg');
    } else if ( post.author === 'jenn'){
      imageLink = require('../../img/jen_pic_blog.jpg');
    } else {
      imageLink = require('../../img/jon_pic_blog.jpg');
    }

    return this.props.posts.slice(0).reverse().map(post => {
      return(
        <div className="item">
          <img className="ui avatar image" src={imageLink} />
          <div className="content">
            <a className="header">{post.title}</a>
            <div className="description">Written by <strong>Jenn</strong> on {post.today}</div>
          </div>
        </div>
      );
    });
  }

  render(){
    return(
      <div>
        <h2>Posts</h2>
        <div className="ui list">{this.renderList()}</div>
      </div>
    );
  }

}

const mapStateToProps = (state) => {
  console.log(Object);
  return {
    posts: Object.values(state.blog),
  };
}

export default connect(mapStateToProps, { fetchPosts })(BlogList);
