import React from 'react';
import { connect } from 'react-redux';
import { editPost, fetchPosts } from '../../actions';
import _ from 'lodash';

import CreateForm from './CreateForm'
import Modal from '../Modal';
import history from '../../history'

class Edit extends React.Component {
  componentDidMount(){
      this.props.fetchPosts();
      var blogId = this.props.location.state.blogId;

  }

  onSubmit = (formValues) => {
    this.props.editPost(this.props.location.state.blogId, formValues);
  };

  renderForm = () => {
    return(
      <div>
        <CreateForm
          onSubmit={this.onSubmit}
          initialValues={_.pick(this.props.posts[this.props.posts.indexOf(this.props.posts.find(obj => obj.id == this.props.location.state.blogId))], 'title', 'description', 'author')}
        />
      </div>
    );
  }

  render(){

    return(
      <Modal
          title="Edit "
          content= {this.renderForm()}
          onDismiss={() => history.push('/blog')}
      />
    );
  };
}

const mapStateToProps = (state) => {
  return {
    posts: Object.values(state.blog),
    currentUserId: state.auth.userId,
  };
}

export default connect(mapStateToProps, { editPost, fetchPosts })(Edit);
