import React from 'react';
import '../css/Bootstrap.css';
import '../css/About.css';

class About extends React.Component{
  state = { chosenClass: []};

  componentDidMount(){
    const chosenClassStart= ["outer", "center", "outer"]
    this.setState({chosenClass: chosenClassStart});
  }


  handleClick = (e, data) => {
    var chosenClassNew = [];
    if(e.currentTarget.id == 0){
      chosenClassNew = ["center", "outer", "outer"]
    }else if(e.currentTarget.id == 2){
      chosenClassNew = ["outer", "outer", "center"]
    } else if(e.currentTarget.id == 1){
      chosenClassNew = ["outer", "center", "outer"]
    }
  }

  renderPanels(tag, id){
    document.body.style = 'background: #40627d;';
      return(
          <div className={`col-sm panel ${tag}`} onClick={this.handleClick} id={id}>
            Hey
          </div>
      );
  }

  render(){
    return (
        <div className="container">
          <div className="row row align-items-center">
            {this.renderPanels(this.state.chosenClass[0], 0)}
            {this.renderPanels(this.state.chosenClass[1], 1)}
            {this.renderPanels(this.state.chosenClass[2], 2)}
          </div>
        </div>
    );
  };

}

export default About;
