import React from 'react';

class ContactForm extends React.Component{
  state = { firstName: '', lastName: '', email: '', body: ''}

  onFormSubmit = event => {
    event.preventDefault();
    this.props.onFormSubmit(this.state.firstName, this.state.lastName, this.state.email, this.state.body);
  }

  onFirstNameChange = event => {
    this.setState({firstName: event.target.value});
  }

  onLastNameChange = event => {
    this.setState({lastName: event.target.value});
  }

  onEmailChange = event => {
    this.setState({email: event.target.value});
  }

  onBodyChange = event => {
    this.setState({body: event.target.value});
  }

  render(){
    return(
      <div>

        <form class="ui form segment">
          <h4 class="ui dividing header">Contact Us</h4>
          <div class="field">
            <label>Name</label>
            <div class="two fields">
              <div class="field">
                <input
                  type="text"
                  placeholder="First Name"
                  value={this.state.firstName}
                  onChange={this.onFirstNameChange}
                />
              </div>
              <div class="field">
                <input
                  type="text"
                  placeholder="Last Name"
                  value={this.state.lastName}
                  onChange={this.onLastNameChange}
                />
              </div>
            </div>
          </div>

          <div class="field">
            <label>E-mail</label>
            <input
              type="email"
              placeholder="joe@schmoe.com"
              value={this.state.email}
              onChange={this.onEmailChange}
            />
          </div>

          <div class="field" style={{marginBottom: 50}}>
            <label>Text</label>
            <textarea value={this.state.body} onChange={this.onBodyChange}></textarea>
          </div>

          <button class="ui primary button" type="submit" onClick={this.onFormSubmit}>Submit</button>

        </form>

      </div>
    );
  }
}

export default ContactForm;
