import {
  CREATE_POST, FETCH_POSTS
} from '../actions/types';

export default (state = {}, action) => {
  switch(action.type) {
    case CREATE_POST:
      return{...state};
    case FETCH_POSTS:
      return{...state, ..._.mapKeys(action.payload, 'id')};
    default:
      return state;
  }
};
