import React from 'react';
import { connect } from 'react-redux';
import { createComment, fetchComments } from '../../../actions';

class CommentsList extends React.Component{
  componentDidMount(){
    this.props.fetchComments();
  }
  renderList(){
    const currentId = this.props.id;
    var postToRead =  this.props.comments.filter(function(posts) {
	     return posts.id == currentId;
    });
    if(postToRead[0]){
      return postToRead[0].comments.slice(0).reverse().map(post => {
        return(
          <div class="comment">
            <a class="avatar">
              <img src="/images/avatar/small/matt.jpg" />
            </a>
            <div class="content">
              <div class="metadata">
                <span class="date">{post.date}</span>
              </div>
              <div class="text">
                {post.comment}
              </div>
              <div class="actions">
                <a class="reply">Reply</a>
              </div>
            </div>
          </div>
        )
      });
    }
  }

  render(){

    return(
      <div className="ui list">{this.renderList()}</div>
    );
  }
}


const mapStateToProps = state => {
  return{
    comments: Object.values(state.comments),
    currentUserId: state.auth.userId
  }
}
export default connect(mapStateToProps, { fetchComments})(CommentsList);
