import React from 'react';
import { connect } from 'react-redux';
import { fetchPosts } from '../../actions';
import Jenn from '../../img/jen_pic_blog.jpg';

class BlogList extends React.Component {
  componentDidMount(){
      this.props.fetchPosts();
  }

  renderList(){
    return this.props.posts.map(post => {
      return(
        <div className="item">
          <img className="ui avatar image" src={Jenn} />
          <div className="content">
            <a className="header">{post.title}</a>
            <br />
            <div className="description">{post.today}</div>
          </div>
        </div>
      );
    });
  }

  render(){
    return(
      <div>
        <h2>Posts</h2>
        <div className="ui list">{this.renderList()}</div>
      </div>
    );
  }

}

const mapStateToProps = (state) => {
  console.log(Object);
  return {
    posts: Object.values(state.blog),
  };
}

export default connect(mapStateToProps, { fetchPosts })(BlogList);
