import React from 'react';
import GoogleAuth from './GoogleAuth';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchPosts } from '../../actions';

import './Blog.css';
import BlogList from './BlogList';
import ShowBlog from './ShowBlog';

class Blog extends React.Component{
  componentDidMount(){
    this.props.fetchPosts();

  }

  renderAdmin(){
    if(this.props.currentUserId == 115089269998867004817){
      return (
        <Link to="blog/create" className="ui button primary">Create Post</Link>
      );
    }
  }

  renderBlog(){
    var blogIds = this.props.posts.map(function (post) {
      return post.title
    });
    console.log(blogIds);
  }

  render(){
    this.renderBlog();
    return(
      <div>
        <div className="ui secondary pointing menu">
          <div className="right menu">
            {this.renderAdmin()}
            <GoogleAuth />
          </div>
        </div>

        <div >
          <div class="row">
            <div class="col-sm-8">
              <ShowBlog />
            </div>
            <div class="col-sm-4 colRight">
              <BlogList />
            </div>
          </div>
        </div>

      </div>
    );
  }
}

const mapStateToProps = state => {
  return{
    posts: Object.values(state.blog),
    currentUserId: state.auth.userId
  }
}

export default connect(mapStateToProps, { fetchPosts })(Blog);
