  }

  render(){
    var blogIds = this.props.posts.map(post => ({ title: post.title, text: post.description, id: post.id }));
    return(
      <div>
        <div className="ui secondary pointing menu">
          <div className="right menu">
            {this.renderAdmin()}
            <GoogleAuth />
          </div>
        </div>

        <div >
          <div class="row">
            <div class="col-sm-8">
              <ShowBlog />
            </div>
            <div class="col-sm-4 colRight">
              <BlogList onBlogClick={this.onBlogClick}/>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

const mapStateToProps = state => {
  return{
    posts: Object.values(state.blog),
    postArray: this.props.posts.map(post => ({ title: post.title, text: post.description, id: post.id })),
    currentUserId: state.auth.userId
  }
}

export default connect(mapStateToProps, { fetchPosts })(Blog);
