import React from 'react';
import GoogleAuth from './GoogleAuth';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchPosts } from '../../actions';

import './Blog.css';
import BlogList from './BlogList';
import ShowBlog from './ShowBlog';

class Blog extends React.Component{
  state = {title: '', description: '', id: '', author: '', date: null}

  constructor(props) {
      super(props);
      var blogIds = this.props.posts.map(post => ({ title: post.title, text: post.description, id: post.id, author: post.author, date: post.today }));

      this.onBlogClick = this.onBlogClick.bind(this)
  }

  componentDidMount(){
    this.props.fetchPosts()

  }

  renderAdmin(){
    if(this.props.currentUserId == 115089269998867004817){
      return (
        <Link to="blog/create" className="ui button primary">Create Post</Link>
      );
    }
  }

  onBlogClick(postId, blogIds){
    var postToRead =  blogIds.filter(function(posts) {
	     return posts.id == postId;
    });
    console.log(postToRead[0].date);
    this.setState({
      title: postToRead[0].title,
      description: postToRead[0].text,
      id: '',
      author: postToRead[0].author,
      date: postToRead[0].date
    })
  }


  render(){

    return(
      <div>
        <div className="ui secondary pointing menu">
          <div className="right menu">
            {this.renderAdmin()}
            <GoogleAuth />
          </div>
        </div>

        <div >
          <div class="row">
            <div class="col-sm-8">
              <ShowBlog
                title={this.state.title}
                description={this.state.description}
                id={this.state.id}
                author={this.state.author}
                date={this.state.date}
              />
            </div>
            <div class="col-sm-4 colRight">
              <BlogList onBlogClick={this.onBlogClick}/>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

const mapStateToProps = state => {
  return{
    posts: Object.values(state.blog),
    currentUserId: state.auth.userId
  }
}

export default connect(mapStateToProps, { fetchPosts })(Blog);
