import React from 'react';
import { connect } from 'react-redux';
import { editPost } from '../../actions';

import CreateForm from './CreateForm'
import Modal from '../Modal';
import history from '../../history'

class Edit extends React.Component {
  componentDidMount(){
    var blogIds = this.props.posts.map(post => ({ title: post.title, text: post.description, id: post.id, author: post.author, date: post.today }));
  }

  onSubmit = (formValues) => {
    this.props.editPost(formValues);
  };

  renderForm = () => {
    return(
      <div>
        <CreateForm onSubmit={this.onSubmit} />
      </div>
    );
  }

  render(){
    return(
      <Modal
          title="Edit "
          content= {this.renderForm()}
          onDismiss={() => history.push('/blog')}
      />
    );
  };
}

export default connect(null, { editPost, fetchPosts })(Edit);
