import React from 'react';
import '../css/Bootstrap.css';
import '../css/About.css';

class About extends React.Component{
  state = { chosenClass: []};

  componentDidMount(){
    const chosenClassStart= ["outer", "center", "outer"]
    this.setState({chosenClass: chosenClassStart});
  }

  changeClass(){
    console.log(this.state.chosenClass);
  }

  handleClick(){
    console.log(this.state.chosenClass);
  }

  renderPanels(tag){
    document.body.style = 'background: #40627d;';
    if(tag === "center"){
      return(
        <div className={`col-sm panel ${tag}`}>
          Hey
        </div>
      );
    } else {
      return(
          <div className={`col-sm panel ${tag}`} onClick={this.handleClick}>
            Hey
          </div>
      );
    }

  }

  render(){
    return (
        <div className="container">
          <div className="row row align-items-center">
            {this.renderPanels(this.state.chosenClass[0])}
            {this.renderPanels(this.state.chosenClass[1])}
            {this.renderPanels(this.state.chosenClass[2])}
          </div>
        </div>
    );
  };

}

export default About;
