import React from 'react';
import '../css/Bootstrap.css';
import '../css/About.css';

class About extends React.Component{
  state = { chosenClass: []};

  componentDidMount(){
    const chosenClassStart= ["outer", "center", "outer"]
    this.setState({chosenClass: chosenClassStart});
  }


  handleClick = (e, data) => {
    var chosenClassNew = [];
    if(e.currentTarget.id == 0){
      chosenClassNew = ["center", "outer", "outer"]
    }else if(e.currentTarget.id == 2){
      chosenClassNew = ["outer", "outer", "center"]
    } else if(e.currentTarget.id == 1){
      chosenClassNew = ["outer", "center", "outer"]
    }
    this.setState({chosenClass: chosenClassNew});
  }


  render(){
    document.body.style = 'background: #40627d;';
    return (
        <div className="container">
          <div className="row row align-items-center">

            <div className={`col-sm panel ${this.state.chosenClass[0]}`} onClick={this.handleClick} id='0'>
              <h3>Consulting</h3>
              <p>Helping teachers and coaches identify concussion and providing them with the tools and protocol to bring students back to school safely.</p>
              <p>Does you school need help?<button className='btn-primary'>Contact Us</button></p>
            </div>

            <div className={`col-sm panel ${this.state.chosenClass[1]}`} onClick={this.handleClick} id='1'>
              <h1>What We Do</h1>
              <p>We provide the knowledge & guidance to bring concussed students back to the classroom & the field.</p>
            </div>

            <div className={`col-sm panel ${this.state.chosenClass[2]}`} onClick={this.handleClick} id='2'>
              <h3>Return To Learn Manual</h3>
              <p>Providing the steps necessary to safely bring students back into the classroom.</p>
            </div>
          </div>
        </div>
    );
  };

}

export default About;
