import React from 'react';

import CommentsList from './CommentsList';
import './Comment.css';
import { connect } from 'react-redux';
import { createComment, fetchComments } from '../../../actions';

class ShowComments extends React.Component{

  render(){
    return(
      <div class="ui comments">
        <CommentsList id={this.props.id} />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return{
    posts: Object.values(state.blog),
    comments: Object.values(state.comments),
    currentUserId: state.auth.userId
  }
}

export default connect(mapStateToProps, { createComment, fetchComments})(ShowComments);
