import React from 'react';
import axios from 'axios';



class ContactForm extends React.Component{
  state = { firstName: '', lastName: '', email: '', body: ''};

  onFormSubmit = event => {
    event.preventDefault();
    axios({
            method: "POST",
            url:"http://localhost:3002/send",
            data: {
                name: this.state.firstName,
                email: this.state.lastName,
                subject: this.state.email,
                messsage: this.state.body
            }
        }).then((response)=>{
            if (response.data.msg === 'success'){
                alert("Message Sent.");
                this.resetForm()
            }else if(response.data.msg === 'fail'){
                alert("Message failed to send.")
            }
        })
  }

  onFirstNameChange = event => {
    this.setState({firstName: event.target.value});
  }

  onLastNameChange = event => {
    this.setState({lastName: event.target.value});
  }

  onEmailChange = event => {
    this.setState({email: event.target.value});
  }

  onBodyChange = event => {
    this.setState({body: event.target.value});
  }

  render(){
    return(
      <div>

        <form className="ui form segment">
          <h4 className="ui dividing header">Contact Us</h4>
          <div className="field">
            <label>Name</label>
            <div className="two fields">
              <div className="field">
                <input
                  type="text"
                  placeholder="First Name"
                  value={this.state.firstName}
                  onChange={this.onFirstNameChange}
                />
              </div>
              <div className="field">
                <input
                  type="text"
                  placeholder="Last Name"
                  value={this.state.lastName}
                  onChange={this.onLastNameChange}
                />
              </div>
            </div>
          </div>

          <div className="field">
            <label>E-mail</label>
            <input
              type="email"
              placeholder="joe@schmoe.com"
              value={this.state.email}
              onChange={this.onEmailChange}
            />
          </div>

          <div className="field" style={{marginBottom: 50}}>
            <label>Text</label>
            <textarea value={this.state.body} onChange={this.onBodyChange}></textarea>
          </div>

          <button className="ui primary button" type="submit" onClick={this.onFormSubmit}>Submit</button>

        </form>

      </div>
    );
  }
}

export default ContactForm;
